mapper.select:
SELECT FROM zf_v2_share_manage a, zf_dict_apptype c WHERE a.OPERATE_TYPE != 3 AND a.DROP_PROTOCOL = c.APP_TYPE
AND a.MESSAGE_NAME LIKE CONCAT("%",#{messageName},"%")
AND date_format(a.CREATE_TIME,'%Y-%m-%d') >= #{startTime}
AND date_format(a.CREATE_TIME,'%Y-%m-%d') <= #{endTime}
GROUP BY a.SHAREID ORDER BY a.MODIFY_TIME DESC

SELECT COUNT(SHAREID)
FROM zf_v2_share_manage
WHERE MESSAGE_NAME = #{messageName}

and	SHAREID != #{shareId,jdbcType=INTEGER}
